import React, { useState } from 'react'
import axios from 'axios';


function AddUserPage() {

  const[formData, setFormData] = useState({
    eeid: '',
    firstname: '',
    lastname: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value,
    }));
  };


  function addUser(e) {
    e.preventDefault();
    const form = e.target;
    const formData = new FormData(form);
    const eeid = Number(formData.get('Eeid'));
    const firstname = formData.get('FirstName');
    const lastname = formData.get('LastName');
    const emp = {'Eeid': eeid, "FirstName": firstname, "LastName": lastname};
    const formJson = Object.fromEntries(formData.entries());
    console.log(formJson);
    console.log(emp);
    axios.post('http://localhost:3001/AddEmployee', emp);
  }

  return (
    <div className='addUserArea'>
        <h1>Add User</h1>
        <form action={addUser}>
            <input name='Eeid' id='Eeid' type='number' pattern='[0-9]*' placeholder='EEID'></input>
            <input name='FirstName' id='FirstName' type='text' placeholder='First Name'></input>
            <input name='LastName' id='LastName' type='text' placeholder='Last Name'></input>
            <button id='addEmployeeButton'>Add User</button>
        </form>
    </div>
  );
}

export default AddUserPage;
