import React, { useState } from 'react'

function AddUserPage() {

  const[formData, setFormData] = useState({
    eeid: '',
    firstname: '',
    lastname: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value,
    }));
  };


  const addUser = () => {
    eeid = document.getElementById('Eeid').value;
    firstname = document.getElementById('FirstName').value;
    lastname = document.getElementById('LastName').value;
    axios.post('http://localhost:3001/AddEmployee', {Eeid: eeid, FirstName: firstname, LastName: lastname})
  }

  return (
    <div className='addUserArea'>
        <h1>Add User</h1>
        <form>
            <input value={formData.eeid} onChange={handleChange} id='Eeid' type='number' pattern='[0-9]*' placeholder='EEID'></input>
            <input value={formData.firstname} onChange={handleChange} id='FirstName' type='text' placeholder='First Name'></input>
            <input value={formData.lastname} onChange={handleChange} id='LastName' type='text' placeholder='Last Name'></input>
            <button onClick={addUser} id='addEmployeeButton'>Add User</button>
        </form>
    </div>
  );
}

export default AddUserPage;
